const{src:src,dest:dest,watch:watch,series:series}=require("gulp"),sass=require("gulp-sass")(require("sass")),prefix=require("gulp-autoprefixer"),minify=require("gulp-clean-css"),terser=require("gulp-terser");function compilescss(){return src("assets/scss/*.scss").pipe(sass()).pipe(prefix("last 2 versions")).pipe(minify()).pipe(dest("dist/css"))}function jsmin(){return src("*.js").pipe(terser()).pipe(dest("dist/js"))}function watchTask(){watch("assets/scss/*.scss",compilescss),watch("assets/*.js",jsmin)}exports.default=series(compilescss,jsmin,watchTask);